CREATE TABLE mod_csi_contents (
    [id] INTEGER PRIMARY KEY,
    [mod_uuid] TEXT NOT NULL,
    [seq] INTEGER NOT NULL,
    [trend] TEXT NOT NULL,
    [focus] TEXT NOT NULL,
    [action] TEXT NOT NULL,
    [prompt] TEXT NOT NULL,
    [created] TEXT NOT NULL DEFAULT ((strftime('%Y-%m-%dT%H:%M:%fZ', 'now', 'utc'))),
    [updated] TEXT,
    UNIQUE (seq, mod_uuid)
);

CREATE TRIGGER update_mod_csi_contents AFTER UPDATE ON mod_csi_contents
BEGIN
    UPDATE mod_csi_contents SET updated = (strftime('%Y-%m-%dT%H:%M:%fZ', 'now', 'utc')) WHERE id=NEW.id;
END;

