CREATE TABLE mod_gmate_conditions (
    [id] INTEGER PRIMARY KEY,
    [mod_uuid] TEXT NOT NULL,
    [flag] TEXT NOT NULL,
    [content] TEXT,
    [created] TEXT NOT NULL DEFAULT ((strftime('%Y-%m-%dT%H:%M:%fZ', 'now', 'utc'))),
    [updated] TEXT,
    UNIQUE (mod_uuid, flag)
);

CREATE TRIGGER update_mod_gmate_conditions AFTER UPDATE ON mod_gmate_conditions
BEGIN
    UPDATE mod_gmate_conditions SET updated = (strftime('%Y-%m-%dT%H:%M:%fZ', 'now', 'utc')) WHERE id=NEW.id;
END;

