CREATE TABLE mod_gmate_prompts (
    [id] INTEGER PRIMARY KEY,
    [mod_uuid] TEXT NOT NULL,
    [content_seq] INTEGER NOT NULL,
    [seq] INTEGER NOT NULL,
    [keycode] INTEGER NOT NULL DEFAULT 0,
    [components] TEXT NOT NULL,
    [prompt] TEXT NOT NULL,
    [a] TEXT NOT NULL,
    [b] TEXT NOT NULL,
    [c] TEXT NOT NULL,
    [d] TEXT NOT NULL,
    [e] TEXT NOT NULL,
    [created] TEXT NOT NULL DEFAULT (strftime('%Y-%m-%dT%H:%M:%fZ', 'now', 'utc')),
    [updated] TEXT,
    UNIQUE (mod_uuid, content_seq, seq)
);

CREATE TRIGGER update_mod_gmate_prompts AFTER UPDATE ON mod_gmate_prompts
BEGIN
    UPDATE mod_gmate_prompts SET updated = (strftime('%Y-%m-%dT%H:%M:%fZ', 'now', 'utc')) WHERE id=NEW.id;
END;

