DROP TABLE IF EXISTS mod_ca_elements; CREATE TABLE mod_ca_elements (
	[id] INTEGER PRIMARY KEY,
	[name] TEXT NOT NULL,
	[domain] TEXT NOT NULL,
	[description] TEXT NOT NULL DEFAULT '',
	[created] TEXT NOT NULL DEFAULT ((strftime('%Y-%m-%dT%H:%M:%fZ', 'now', 'utc'))),
	[updated] TEXT
);

CREATE TRIGGER update_mod_ca_elements AFTER UPDATE ON mod_ca_elements
    BEGIN UPDATE mod_ca_elements SET updated = (strftime('%Y-%m-%dT%H:%M:%fZ', 'now', 'utc')) WHERE id=NEW.id;
END;

